import logo from './logo.svg';
import './App.css';
import { Button, Input } from '@mui/material';
import { Spacing, Stack } from '@mui/system';
import { useState } from 'react';
import {addNumber, retrieveData} from './service/service' ;
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
function App() {
  const [show, setShow] = useState(false);
  const [show_1, showTable] = useState(false);
  const [numberToAdd, setNumberToAdd] = useState(0);
  const [tableData, setTableData] = useState([])
  let tableRows = [];
  return (
    <div className="App">
      <Stack spacing={1}>
        <div>
      <Button variant="contained" sx={{ m: 8 }} onClick={() => {
        setShow(prev => !prev);
        showTable(false);
        }}>Add</Button>
      <Button variant="contained" onClick={() => {
        showTable(prev => !prev)
        setShow(false);
        if (!show_1)
        {
          retrieveData().then((data) => {
              setTableData(data);
              data.array.forEach(element => {
                tableRows.push(<TableCell align="right">{element}</TableCell>);
              });
            })
        }
        }}>Sample 10</Button>
      </div>
      {show && 
      <Stack spacing={1} direction="row">
      <Input placeholder='Write the number you want to add here' 
      onChange={(inputValue) => { setNumberToAdd(inputValue.target.value)}}
      onSubmit/>
      <Button variant="outlined" onClick={() => {
        addNumber(numberToAdd)
      }}>Submit</Button>
            </Stack>
      }
      {show_1 &&
      <Stack spacing={1} direction="row">
      <TableContainer component={Paper}>
      <Table sx={{ minWidth: 65 }} aria-label="simple table">
        <TableHead>
          <TableRow>

            {tableData}
          </TableRow>
        </TableHead>
        </Table>
    </TableContainer>
    </Stack>
    }
      </Stack>
    </div>
  );
}

export default App;
